import styled from "styled-components";

function Modal({ children }) {
  return (
    <ModalBackground>
      <StyledModal>{children}</StyledModal>
    </ModalBackground>
  );
}

const ModalBackground = styled.div`
  height: 100vh;
  width: 100vw;
  background: rgba(0, 0, 0, 0.2);
  position: fixed;
  top: 0;
  left: 0;
`;

const StyledModal = styled.div`
  width: 30vw;
  background: #fff;
  padding: 2rem;
`;

export default Modal;
