import { useState } from "react";
import styled from "styled-components";

function FeedCollectable() {
  const [likes, setLikes] = useState(100);
  const [hasLiked, setHasLiked] = useState(true);
  return (
    <StyledFeedCollectable>
      <div>
        <User>
          <span></span>RYANWALKER
        </User>
        <Title>Title</Title>
        <Description>This is a description etc etc etc</Description>
        <Category>Vinyl</Category>
      </div>
      <div>
        <ImageContainer></ImageContainer>
        <LikesBar>
          {likes} likes{" "}
          <span>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              viewBox="0 0 24 24"
              fill={hasLiked ? "red" : "none"}
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
              className="feather feather-heart"
            >
              <path d="M20.84 4.61a5.5 5.5 0 0 0-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 0 0-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 0 0 0-7.78z"></path>
            </svg>
          </span>
        </LikesBar>
      </div>
    </StyledFeedCollectable>
  );
}

const StyledFeedCollectable = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  column-gap: 2rem;
  margin-bottom: 6rem;
`;

const User = styled.div`
  font-weight: 700;
  text-transform: uppercase;
  font-size: 2.4rem;
  display: flex;
  align-items: center;
  margin-bottom: 3rem;

  span {
    margin-right: 1rem;
    border-radius: 50%;
  }
`;

const Title = styled.div`
  font-size: 2.4rem;
  font-weight: 700;
`;

const Description = styled.div`
  font-size: 1.8rem;
  margin-bottom: 3rem;
`;

const Category = styled.div`
  font-size: 2rem;
  text-transform: uppercase;
  font-weight: 700;
`;

const ImageContainer = styled.div`
  height: 200px;
  width: 100%;
  border-radius: 3px;
  background: #c4c4c4;
  margin-bottom: 1.5rem;
`;

const LikesBar = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 1.8rem;

  span {
    height: 26px;
    width: 26px;
    border-radius: 50%;
    background: #c4c4c4;
  }
`;

export default FeedCollectable;
